import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import WithServer from '../hoc';
//import { loaded } from '../../actions'
import { loaded, createNewUser } from '../../redux/user/actions'
import Spinner from '../spinner'

import './registrationForm.sass';

const RegistrationForm = ({ Server }) => {
    //useSelector
    const loading = useSelector(state => state.loading)

    //useDispatch
    const dispatch = useDispatch()

    //useState
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [errorRegistration, setError] = useState({ error: false, message: '' })

    //useHistory
    let history = useHistory();

    const nav = (link) => {
        history.push(link)
    }

    useEffect(() => dispatch(loaded()), [dispatch, loading])

    const registerError = () => {
        if (name.length === 0) {
            setError({ error: true, message: 'Введите имя' })
        }

        setTimeout(() => setError({ error: false, message: '' }))
    }

    const registerUser = () => {
        //Server.createNewUser(email, password, name, history, dispatch)
        dispatch(createNewUser(email, password, name, history))
        registerError()
    }


    if (loading) {
        return <Spinner />
    }

    return (
        <div className="registration-form">
            <div className="modal-registration">
                <form onSubmit={(e) => e.preventDefault()}>
                    <div className="form-group">
                        <label>Name</label>
                        <input
                            type="name"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter name"
                            value={name}
                            onChange={(e) => setName(e.target.value)} />
                        <small className="form-text text-muted">What is your name ?</small>
                    </div>
                    <div className="form-group">
                        <label >Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)} />
                        <small className="form-text text-muted">We'll never share your email with anyone else.</small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)} />
                        <small className="form-text text-muted">Password must be at least 5 characters. <br /> We will
                 never share your password with anyone.</small>
                    </div>
                    <button type="submit" className="cancel" onClick={() => nav(`/list/page${1}/?limit=10&skip=${0}`)}>Сancel</button>
                    <button type="submit" className="confirm" onClick={registerUser}>Sign Up</button>
                    {errorRegistration.error && <p>{errorRegistration.message}1</p>}
                </form>
            </div>
        </div>
    )
}

export default WithServer()(RegistrationForm);
