import React from 'react';

import './editor.sass';

const Editor = () => {
    const defaultAvatar = 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'

    return (
        <div className='editor'>
            <header className='editor-header'>
                <div className='editor-avatar'>
                    <img alt='img' src={defaultAvatar} />
                    <label htmlFor='file-upload' className='inp-upload' >
                        <i class="fas fa-camera"></i>
                        <input type='file' id="file-upload" />
                    </label>
                </div>
                <p>Bezdelnik</p>
                {/*<button className='btn btn-outline-primary'>Change name</button>
                <button className='btn btn-outline-primary'>Change password</button>*/}
            </header>
            <div className='editor-body'>

            </div>
        </div>
    )
}

export default Editor;
