import axios from 'axios';

export default class Server {

    async loginUser(email, password) {
        try {
            const _response = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", {
                email,
                password,
            });
            console.log('üëâ Returned data:', _response);
            return _response
        } catch (e) {
            console.log(`üò± Axios request failed: ${e}`);
        }

    }

    async authorizationUser(token) {
        try {
            const res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth/user', { headers: { "Authorization": `Bearer ${token}` } })
            localStorage.setItem('token', token)
            return res
        } catch (e) {
            console.log(e)
        }

    }

    async createNewUser(email, password, name) {
        try {
            const _response = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/users", {
                email,
                password,
                name
            });
            console.log('üëâ Returned data:', _response);
            return _response
        } catch (e) {
            console.log(e.response);
            console.log(`üò± Axios request failed: ${e}`);

        }
    }

    async getAllUsers() {
        try {
            const res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/users')
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response);
            console.log(`üò± Axios request failed: ${e}`);
        }
    }
}
