import React from 'react';
import { useHistory } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import WithServer from '../hoc';

import './loginForm.css';


const LoginForm = ({ Server }) => {

    const state = useSelector(state => state);

    let history = useHistory();

    const nav = (link) => {
        history.push(link)
    }

    let signInUsers = {
        email: '',
        password: ''
    }

    const emailUser = (e) => {
        signInUsers.email = e.target.value
    }

    const passwordUser = (e) => {
        signInUsers.password = e.target.value
    }

    return (
        <div className="post-list">
            <div className="modal-authorization">
                <form>
                    <div className="form-group">
                        <label>Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            title='Enter your email'
                            onChange={emailUser} />
                        <small className="form-text text-muted"></small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            title='Enter your password'
                            onChange={passwordUser} />
                        <div className="auth-group">
                            <button className="forgotPass authHover" onClick={(e) => e.preventDefault()}>Forgot password</button>
                            <button className="signUp authHover" onClick={() => nav("/signUp")}>sign up</button>
                        </div>

                        <small className="form-text text-muted"></small>
                    </div>
                    <button type="submit" className="btn btn-outline-primary cancel" onClick={() => nav("/")}>Сancel</button>
                    <button type="submit" className="btn btn-primary confirm" onClick={(e) => {
                        e.preventDefault();
                        Server.loginUser(signInUsers)
                            .then(resp => {
                                if (resp.statusText === "OK") {
                                    nav("/list")
                                }
                                console.log(resp.data.token)
                                Server.giveUser(resp.data.token)
                                    .then(res => {
                                        console.log(res.data)
                                        state.user.push(res.data)
                                        console.log(state)

                                    })
                            })

                    }}>Sign in</button>
                </form>
            </div>
        </div>
    )
}


export default WithServer()(LoginForm);
