import React from 'react';
import { useHistory } from 'react-router-dom';
import { connect } from 'react-redux';
import './registrationForm.css';
import { autor } from '../../actions';

const LoginForm = ({ autor, state }) => {

    let history = useHistory();

    const signUp = () => {
        history.push("/signUp")
    }

    const main = () => {
        history.push("/list");
    }

    return (
        <div className="post-list">
            <div className="modal-registration">
                <form>
                    <div className="form-group">
                        <label>Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            title='Enter your email' />
                        <small className="form-text text-muted"></small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            title='Enter your password' />
                        <small className="form-text text-muted"></small>
                    </div>
                    <button type="submit" className="btn btn-primary up" onClick={signUp}>Sign up</button>
                    <button type="submit" className="btn btn-primary in" onClick={(e) => {
                        e.preventDefault();
                        (autor());
                        if (state.autor) {
                            main();
                        }
                        console.log(state)
                    }}>Sign in</button>
                </form>
            </div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        state
    }

}

const mapDispatchToProps = {
    autor
}

export default (connect(mapStateToProps, mapDispatchToProps)(LoginForm));
