import React, { Suspense, useEffect, useRef, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
import { addUser } from '../../actions';
import Spinner from '../spinner'

import './editor-profile.sass';

const EditorProfile = ({ Server }) => {
    const defaultAvatar = 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'

    const dispatch = useDispatch()
    const [name, setName] = useState('')
    const [currentUser, setUser] = useState({})
    const user = useSelector(state => state.user)

    const refAvatar = useRef()

    useEffect(() => setUser(user), [user])

    const isAuthorized = localStorage.getItem('token');

    const changeName = (id, token, name) => {
        //spread operator
        Server.updateUserName(id, token, name)
            .then(res => {
                setUser({ ...currentUser, name })
                setName('')
                localStorage.setItem('user', JSON.stringify(res.data))
                dispatch(addUser(res.data))
            })
    }

    useEffect(() => {
        //if (user) {
        Server.getImage(refAvatar, user.avatar, defaultAvatar, '')
        //}

    }, [Server, user])


    return (
        <div className='container-editor-profile' onClick={() => console.log(user.avatar)}>
            <form className='editor-profile' onSubmit={(e) => e.preventDefault()}>
                <header className='editor-header'>
                    <div className='editor-avatar'>
                        {/*<Suspense fallback={<Spinner />}>*/}
                        <img alt='img' ref={refAvatar} />
                        {/*</Suspense>*/}
                        <label htmlFor='file-upload' className='inp-upload' >
                            <i className="fa fa-camera"></i>
                            <input
                                type='file'
                                id="file-upload"
                                //onChange={(e) => setFile(e.target.files[0])}
                                onChange={(e) => {
                                    let formData = new FormData()
                                    formData.append('avatar', e.target.files[0])
                                    Server.updateUserAvatar(user._id, isAuthorized, formData)
                                        .then(res => {
                                            localStorage.setItem('user', JSON.stringify(res.data))
                                            dispatch(addUser(res.data))
                                        })
                                }}
                            />
                        </label>
                    </div>
                    <p className='editor-name'>{currentUser.name}</p>
                </header>
                <div>
                    <label >Name:</label>
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Change your name"
                        title='Enter your new name'
                        value={name}
                        onChange={(e) => {
                            setName(e.target.value)
                        }}
                    />
                </div>
                <button className='btn btn-primary save' onClick={() => changeName(user._id, isAuthorized, name)}>Save</button>
            </form>
        </div>
    )
}

export default WithServer()(EditorProfile);
