import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
import { useHistory, useParams } from 'react-router-dom';
import { posts, loaded, fault } from '../../actions';
import Spinner from '../spinner';
import SimpleSnackbar from '../snackbar';
import Error from '../error';
//import PaginationOutlined from './pagination'

import './post-list-item.sass';


const PostListItem = ({ Server }) => {
    //useParams
    const { skip } = useParams();
    const { page } = useParams();



    //useSelector
    const postOnPage = useSelector(state => state.postOnPage)
    const loading = useSelector(state => state.loading)
    const isSnackbar = useSelector(state => state.isSnackbar)
    const error = useSelector(state => state.error)

    //useDispatch
    const dispatch = useDispatch()

    //useState
    const [countBtn, setCountBtn] = useState([]);
    const [activeNum, setActiveNum] = useState(+skip)

    //useHistory
    const history = useHistory();

    const navHistory = (url) => {
        history.push(url)
    }

    useEffect(() => {
        Server.getAllPosts(skip)
            .then(res => {
                dispatch(posts(res.data))
                dispatch(loaded())
            })
            .catch(er => dispatch(fault()))
    }, [Server, dispatch, error, skip])

    useEffect(() => {
        if (!loading) {
            Server.getAllPosts(0, 0)
                .then(res => {
                    setCountBtn(res.data.filter((item, i) => res.data[i / 10]))
                })
        } else {
            return
        }
    }, [Server, loading])


    let idx = 0;

    const postList = postOnPage.map((postItem, i) => {
        idx++

        return (
            <div className="post" key={idx}>
                <div className="post-list-item" >
                    <div className="cards">
                        <p className="title">{postItem.title}</p>
                        <ul className="full-text">{postItem.description}</ul>
                        <div className="full-post">
                            <button
                                className='view'
                                onClick={() => navHistory(`/post/${postItem._id}`)}>
                                View
                            </button>
                        </div>
                    </div>
                </div>
            </div>

        )
    })

    if (error) {
        return <Error />
    }

    return (
        <>
            <div className="post-list" onClick={() => console.log(page)}>
                <p>Skip: {new URLSearchParams(this.props.location.search).get("skip")}</p>
                {
                    !loading
                        ? postList
                        : <Spinner />
                }
            </div>
            {postList.length !== 0 &&
                <div className='nav-posts'>
                    <div className='nav-posts-overflow'>
                        {countBtn.map((item, i) => {
                            return <button
                                key={idx++}
                                className={`btn-nav-posts ${activeNum === i * 10 ? 'active-btn' : ''}`}
                                onClick={(e) => {
                                    setActiveNum(i * 10)
                                    //navHistory(`/list/${i * 10}`)
                                    navHistory(`/list/page${i + 1}/?limit=10&skip=${i * 10}`)
                                }}
                            >{i + 1}</button>
                        })}
                    </div>
                </div>
            }
            {/*<PaginationOutlined />*/}
            <SimpleSnackbar isSnackbar={isSnackbar} color={"success"} message={'Deleted successful!'} />
        </>
    )
}


export default WithServer()(PostListItem);
