import React, { useRef, useState, useEffect } from 'react';
import WithServer from '../../hoc';
//import { addUser, fault } from '../../../actions';
import { useDispatch, useSelector } from 'react-redux';
import { loginUser } from '../../../redux/user/actions'

import LoginForm from '../../login-form';

import './modalAuthorized.sass';

const ModalAuthorized = ({ showModal, setShowModal }) => {
    //useSelector
    const error = useSelector(state => state.error)

    //useRef
    const refModalLiked = useRef()
    const refModolLikedBtn = useRef()
    const refLoginForm = useRef()
    const refLoginFormBtn = useRef()


    //useDispatch
    const dispatch = useDispatch()

    //useState
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [showLogin, setShowLogin] = useState(false)

    //useHistory

    const customLogin = e => {
        e.preventDefault()
        //Server.loginUser(email, password, nav, dispatch)
        //    .then(() => setShowLogin(false))
        dispatch(loginUser(email, password))
        setShowLogin(false)
    }


    useOnClickOutside(refModalLiked, (e) => {
        if (refModolLikedBtn.current && !refModolLikedBtn.current.contains(e.target))
            setShowModal('hidden')
    });

    useOnClickOutside(refLoginForm, (e) => {
        if (refLoginFormBtn.current && !refLoginFormBtn.current.contains(e.target))
            setShowLogin(false)
    });

    function useOnClickOutside(ref, handler) {
        useEffect(
            () => {
                const listener = (event) => {
                    if (!ref.current || ref.current.contains(event.target)) {
                        return;
                    }
                    handler(event);
                };
                document.addEventListener("mousedown", listener);
                document.addEventListener("touchstart", listener);
                return () => {
                    document.removeEventListener("mousedown", listener);
                    document.removeEventListener("touchstart", listener);
                };
            },
            [ref, handler]
        );
    }

    return (
        <>
            <div className={`modal-liked ${showModal}`} ref={refModalLiked}>
                <div className='header-modal-liked'>
                    <p className='modal-liked-title'>Do u like post?</p>
                    <button className='close-modal-like' ref={refModolLikedBtn} onClick={() => setShowModal('hidden')}>X</button>
                </div>
                <p className='login-first'>login first</p>
                <hr />
                <button className='login-modal-liked' onClick={() => {
                    setShowLogin(true)
                    setShowModal('hidden')
                }}>Sign in</button>
            </div>
            {/*<LoginForm />*/}


            <div className={`small-login-form ${showLogin ? 'show' : 'hidden'}`}>
                <div className="small-modal-authorization" ref={refLoginForm}>
                    <button className='close-login' ref={refLoginFormBtn} onClick={() => setShowLogin(false)}>X</button>
                    <form onSubmit={customLogin}>
                        <div className="form-group">
                            <label>Email address</label>
                            <input
                                type="email"
                                className="form-control"
                                aria-describedby="emailHelp"
                                placeholder="Enter email"
                                title='Enter your email'
                                value={email}
                                onChange={(e) => { setEmail(e.target.value) }} />
                            <small className="form-text text-muted"></small>
                        </div>
                        <div className="form-group">
                            <label>Password</label>
                            <input
                                type="password"
                                className="form-control"
                                placeholder="Password"
                                title='Enter your password'
                                value={password}
                                onChange={(e) => setPassword(e.target.value)} />
                            <div className="auth-group">
                                <a className="forgotPass authHover" href=' '>Forgot password</a>
                                <a className="signUp authHover" href='/signUp'>sign up</a>
                            </div>

                            <small className="form-text text-muted"></small>
                        </div>
                        <button className="cancel" onClick={() => setShowLogin(false)}>Сancel</button>
                        <input type="submit" className="confirm" value="Sign In" />
                        {error && <p className='error'>Что-то пошло не так....</p>}
                    </form>
                </div>
            </div >
        </>
    )
}

export default WithServer()(ModalAuthorized);
