import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getAllUsers } from '../../redux/user/actions';

import './user-list.sass'

const UserList = () => {
    //Dispatch
    const dispatch = useDispatch()

    //useSelector
    const allUsers = useSelector(state => state.userState.allUsers)

    useEffect(() => {
        if (allUsers.length === 0) {
            dispatch(getAllUsers())
        }
    }, [allUsers, dispatch])

    return (
        <div className='user-list-container'>
            <button onClick={() => {
                dispatch(getAllUsers())
            }}>Click for get allUsers</button>
            {allUsers.map(user => {
                return <div className='user-list' key={user._id}>
                    <div className='user-list-header'>
                        <img src='/' alt='avatar' />
                    </div>
                    <div>
                        <p>Name: {user.name}</p>
                        <p>Email: {user.email}</p>
                        <p>Date created: {user.dateCreated}</p>
                        <button>Go to posts user</button>
                    </div>
                </div>
            })}
        </div>
    )
}

export default UserList;
