const initialState = {
    error: false,
    login: false,
    user: null,
    allUsers: null
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'NEW_USER':
            return {
                ...state,
                user: action.payload
            }
        case 'LOGIN':
            return {
                ...state,
                login: true
            }
        case 'LOGOUT':
            return {
                ...state,
                login: false,
                user: null
            }
        case 'ERROR':
            return {
                ...state,
                error: action.payload
            }
        case 'ALL_USERS':
            return {
                ...state,
                allUsers: action.payload
            }
        default:
            return state
    }
}


export default reducer;
