import React, { useRef } from 'react';

const ModalAuthorized = ({ cl }) => {
    const ref = useRef()
    let clazz = cl;

    return (
        <div className={`test ${clazz}`} ref={ref}>
            <div className='close-modal'>
                <p className='test-title'>Do u like post?</p>
                <button className='close-modal-like' onClick={() => {
                    ref.current.classList.remove('block')
                    ref.current.classList.add('hiden')
                }}>X</button>
            </div>
            <p className='login-first'>login first</p>
            <hr />
            <button className='sign-in-modal'>Sign in</button>
        </div>
    )
}

export default ModalAuthorized;
