import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import WithServer from '../hoc';
//import { addUser, fault } from '../../actions';
import SimpleSnackbar from '../snackbar';
import Error from '../error';

import './loginForm.sass';

import { loginUser } from '../../redux/user/actions'


const LoginForm = ({ show, setShow }) => {
    //useState
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    //useDispatch
    const dispatch = useDispatch()

    //useHistory
    let history = useHistory();

    //useSelector
    //const error = useSelector(state => state.error)
    //const isSnackbar = useSelector(state => state.isSnackbar)
    //new
    const error = useSelector(state => state.userState.error)
    const deleteUser = useSelector(state => state.userState.deleteUser)

    const nav = (link) => {
        history.push(link)
    }

    const customLogin = () => {
        dispatch(loginUser(email, password, nav))
    }

    const cancelEvent = () => {
        if (show) {
            setShow(false)
        } else {
            nav(`/list/page${1}/?limit=10&skip=${0}`)
        }
    }


    if (error) {
        return <Error />
    }



    return (
        <div className={`login-form `} onClick={() => console.log(show)}>
            <div className={`modal-authorization ${show && 'back-shadow'}`}>
                <form onSubmit={(e) => e.preventDefault()}>
                    <div className="form-group">
                        <label>Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            title='Enter your email'
                            value={email}
                            onChange={(e) => setEmail(e.target.value)} />
                        <small className="form-text text-muted"></small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            title='Enter your password'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)} />
                        <div className="auth-group">
                            <button className="forgotPass authHover" onClick={(e) => e.preventDefault()}>Forgot password</button>
                            <button className="signUp authHover" onClick={() => nav("/signUp")}>sign up</button>
                        </div>

                        <small className="form-text text-muted"></small>
                    </div>
                    <button type="submit" className="cancel" onClick={() => cancelEvent()}>Сancel</button>
                    <button type="submit" className="confirm" onClick={customLogin}>Sign In</button>
                    {error ? <p className='error'>Что-то пошло не так....</p> : <p></p>}
                </form>
            </div>
            <SimpleSnackbar isSnackbar={deleteUser} color={'success'} message={'Deleted successful!'} />
        </div >
    )
}


export default WithServer()(LoginForm);
