import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import { loaded, fault } from '../../actions';
import { useHistory } from 'react-router-dom';
import WithServer from '../hoc';
import './create-post-form.css'

const CreatePostForm = ({ Server }) => {

    //useSelector
    const loading = useSelector(state => state.loading)
    const error = useSelector(state => state.error)

    //useState
    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [fullText, setFullText] = useState('')
    const [message, setMessage] = useState('')

    //localStorage
    const isAuthorized = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user'));

    //useHistory
    let history = useHistory();

    const nav = (link) => {
        history.push(link)
    }

    const createPost = () => {
        Server.createNewPost(title, fullText, description, isAuthorized)
            .then(res => {
                if (res.status >= 400) {
                    setMessage(res.data.error[0].message)
                } if (res.status === 200) {
                    setMessage('Success')
                    setTimeout(() => nav(`/user-posts/${user._id}`), 1500)
                }
            })
    }



    return (
        <div className='container-create-form'>
            <form className='form-create' onSubmit={(e) => e.preventDefault()}>
                <div className="form-group">
                    <label >Title</label>
                    <input type="text"
                        className="form-control"
                        placeholder="Title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)} />
                </div>
                <div className="form-group">
                    <label>Description</label>
                    <input
                        type="text"
                        className="form-control"
                        placeholder="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)} />
                </div>
                <div className="form-group">
                    <label htmlFor="exampleFormControlTextarea1">Full text</label>
                    <textarea
                        className="form-control"
                        id="exampleFormControlTextarea1"
                        rows="3"
                        placeholder='Full text'
                        value={fullText}
                        onChange={(e) => setFullText(e.target.value)} >
                    </textarea>
                </div>
                <p className='message'>{message}</p>
                <button className='btn-create' onClick={createPost}>Create post</button>
            </form>
        </div>
    )
}

export default WithServer()(CreatePostForm);
