import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import WithServer from '../hoc';
import { addUser, someErorr } from '../../actions';
import SimpleSnackbar from '../snackbar';

import { useSelector } from 'react-redux';
import './loginForm.css';

const LoginForm = ({ Server }) => {
    //useState
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    //useDispatch
    const dispatch = useDispatch()

    //useHistory
    let history = useHistory();

    //useSelector
    const error = useSelector(state => state.error)
    const isSnackbar = useSelector(state => state.isSnackbar)


    const nav = (link) => {
        history.push(link)
    }

    const customLogin = () => {
        Server.loginUser(email, password)
            .then(res => {
                nav(`/list/${0}`)
                localStorage.setItem('token', res.data.token)
                if (localStorage.getItem('token')) {
                    Server.authorizationUser(localStorage.getItem('token'))
                        .then(res => {
                            dispatch(addUser(res.data))
                            let saveUser = JSON.stringify(res.data)
                            localStorage.setItem('user', saveUser)
                        })
                }
                dispatch(someErorr(false))
            })
            .catch(e => {
                nav('/login')
                console.log(e)
                dispatch(someErorr(true))
            })
    }

    return (
        <div className="login-form">
            <div className="modal-authorization">
                <form onSubmit={(e) => e.preventDefault()}>
                    <div className="form-group">
                        <label>Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            title='Enter your email'
                            value={email}
                            onChange={(e) => { setEmail(e.target.value) }} />
                        <small className="form-text text-muted"></small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            title='Enter your password'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)} />
                        <div className="auth-group">
                            <button className="forgotPass authHover" onClick={(e) => e.preventDefault()}>Forgot password</button>
                            <button className="signUp authHover" onClick={() => nav("/signUp")}>sign up</button>
                        </div>

                        <small className="form-text text-muted"></small>
                    </div>
                    <button type="submit" className="cancel" onClick={() => nav(`/list/${0}`)}>Сancel</button>
                    <button type="submit" className="confirm" onClick={customLogin}>Sign In</button>
                    {error ? <p className='error'>Что-то пошло не так....</p> : <p></p>}
                </form>
            </div>
            <SimpleSnackbar isSnackbar={isSnackbar} color={'success'} message={'Deleted successful!'} />
        </div >
    )
}


export default WithServer()(LoginForm);
