import React, { useEffect, useRef, useState } from 'react';
import { useSelector } from 'react-redux';
import WithServer from '../hoc';
import ModalAuthorized from './modalAuthorized'
import { useHistory, useParams } from 'react-router';
import Spinner from '../spinner';

import { useDispatch } from 'react-redux';
import { loaded } from '../../actions';

import prof from './no-avatar.jpg'
import deletProf from './delete-user.jpg'

import './post-list.css';

const PostList = ({ Server }) => {
    const defaultAvatar = 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'

    //Hook

    // useState
    const [post, setPost] = useState({});
    const [postUser, setPostUser] = useState({});
    const [date, setDate] = useState('')
    const [heart, setHeart] = useState('')
    //useDispatch & useSelector
    const dispatch = useDispatch()
    const loading = useSelector(state => state.loading)

    // useHistory & useParams
    const history = useHistory()
    const { id } = useParams()

    //Ref
    const refHeart = useRef()
    const refAvatar = useRef()
    const refImagePost = useRef()

    //local
    const isAuthorized = localStorage.getItem('token');
    const isAuthorizedUser = JSON.parse(localStorage.getItem('user'))

    //DateCreated
    const dateСorrector = (postData) => {
        let date = postData
        let newDate = date.replace(/[T-Z]/g, ' ')
        setDate(newDate.replace(/\..*/, ''))
    }

    // If an authorized user has a like on a post
    function saveLike(likedUser, e) {
        const idxLike = likedUser.indexOf(isAuthorizedUser._id)
        if (idxLike >= 0) {
            e.classList.add('liked')
        } else {
            e.classList.remove('liked')
        }
    }

    // Func get post and user
    useEffect(() => {
        Server.getPost(id)
            .then(res => {
                dispatch(loaded())
                setPost(res.data)
                setHeart(res.data.likes.length)
                if (isAuthorized) {
                    saveLike(res.data.likes, refHeart.current)
                }
                dateСorrector(res.data.dateCreated)
                Server.getImage(refImagePost, res.data.image, '', res.data.description)
                Server.getUser(res.data.postedBy)
                    .then(res => {
                        Server.getImage(refAvatar, res.data.avatar, defaultAvatar)
                        setPostUser(res.data)
                    })
                    .catch(res => setPostUser(null))
            })
        //eslint-disable-next-line react-hooks/exhaustive-deps
    }, [Server, dispatch, id, isAuthorized])

    // number of likes on the post
    const postLike = async (e) => {
        await Server.getPost(post._id)
            .then(res => {
                if (res.data.likes.length !== heart) {
                    setHeart(res.data.likes.length)
                    saveLike(res.data.likes, e)
                }
            })
    }

    // func setlike 
    async function setLike(id, token, e) {
        await Server.setLikePost(id, token)
        postLike(e)
    }

    const [modal, setModal] = useState('')


    if (loading || !post) {
        return <Spinner />
    }

    return (
        <div className="item">
            <div className="item-post">
                <div className="post-item-list">
                    {postUser
                        ?
                        <div className="info-user">
                            <img src={prof} className="user-avatar cntr" alt="prof" ref={refAvatar} />
                            <p> {postUser.name}</p>
                        </div>
                        :
                        <div className="info-user">
                            <img src={deletProf} className="user-avatar cntr" alt="prof" />
                            <p className="delete-user"> User has been deleted</p>
                        </div>}
                    <div className="info-post">
                        <div className='header-post'>
                            <p className="title">{post.title}</p>

                            {isAuthorized && post.postedBy === isAuthorizedUser._id &&
                                <button className='btn-update-post ' onClick={() => history.push(`/editor-posts/${id}`)}>
                                    Edit post
                                </button>}

                        </div>
                        <img src='' alt={post.description} className="post-image" ref={refImagePost} />
                        <ul className="full-text">{post.fullText}</ul>
                        <div className="footer-post">
                            <span className="date">DATE CREATE : <br />{date}</span>
                            <div className="interaction">
                                <button
                                    className='like'
                                    onClick={() => {
                                        isAuthorized
                                            ? setLike(post._id, isAuthorized, refHeart.current)
                                            : setModal('show-flex')
                                    }
                                    }><i ref={refHeart} className="fa fa-heart"></i></button>{heart}
                            </div>
                        </div>
                    </div>

                </div>

            </div>
            <ModalAuthorized cl={modal} setModal={setModal} />
        </div>
    )
}


export default WithServer()(PostList);
