import React, { useEffect, useRef, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
import { useHistory, useParams } from 'react-router-dom';
import { posts, loaded } from '../../actions';
import Spinner from '../spinner';
import SimpleSnackbar from '../snackbar';

import './post-list-item.css';


const PostListItem = ({ Server }) => {
    const btnRef = useRef()

    const allPosts = useSelector(state => state.allPosts)
    const loading = useSelector(state => state.loading)
    const isSnackbar = useSelector(state => state.isSnackbar)
    const dispatch = useDispatch()

    const [btnNav, setBtnNav] = useState([]);

    const { skip } = useParams();

    let history = useHistory();

    const navHistory = (url) => {
        history.push(url)
    }

    useEffect(() => {
        Server.getAllPosts(skip)
            .then(res => {
                dispatch(posts(res.data))
                dispatch(loaded())
            })
            .catch(er => console.log(er))
    }, [Server, dispatch, skip])

    const goToUserPost = (idPost) => {
        navHistory(`/post/${idPost}`)
    }

    let idx = 0;
    const [num, setNum] = useState(+skip)
    //??????????????????????????????????????????
    useEffect(() => {
        Server.getAllPosts(0, 0)
            .then(res => {
                setBtnNav(res.data.filter((item, i) => res.data[i / 5]))
            })
    }, [Server, btnNav])
    //console.log(num)
    const test = () => {
        //const newBtnNav = btnNav.filter((item, i) => btnNav[i / 5])
        //newBtnNav.map(item => console.log(1))
        console.log(btnNav)
    }
    //???????????????????????????????????????????????????????
    const postList = allPosts.map((postItem, i) => {
        idx++

        return (
            <div className="post" key={idx} onClick={() => test()}>
                <div className="post-list-item" >
                    <div className="cards">
                        <p className="title">{postItem.title}</p>
                        <ul className="full-text">{postItem.description}</ul>
                        <div className="full-post">
                            <p className='text'>View full post</p>
                            <button
                                className='delete'
                                onClick={() => goToUserPost(postItem._id)}>
                                <i className="fa fa-arrow-circle-right fa-2x"></i>
                            </button>
                        </div>
                    </div>
                </div>
            </div>

        )
    })


    return (
        <>
            <div className='nav-posts'>
                <button className='btn-nav-posts' onClick={() => navHistory(`/list/${0}`)}>1</button>
                <button className='btn-nav-posts' onClick={() => navHistory(`/list/${5}`)}>2</button>
                <button className='btn-nav-posts' onClick={() => navHistory(`/list/${10}`)}>3</button>
                <button className='btn-nav-posts' onClick={() => navHistory(`/list/${15}`)}>4</button>
                <button className='btn-nav-posts' onClick={() => navHistory(`/list/${20}`)}>5</button>
                <button className='btn-nav-posts' onClick={() => navHistory(`/list/${25}`)}>6</button>
                <button className='btn-nav-posts' onClick={() => navHistory(`/list/${30}`)}>7</button>
                <button className='btn-nav-posts' onClick={() => navHistory(`/list/${35}`)}>8</button>
                <button className='btn-nav-posts' onClick={() => navHistory(`/list/${40}`)}>9</button>
                <button className='btn-nav-posts' onClick={() => navHistory(`/list/${45}`)}>10</button>
            </div>
            <div className="post-list">
                {
                    !loading
                        ? postList
                        : <Spinner />
                }
            </div>
            <div className='nav-posts'>
                {/*<button className='btn-nav-posts' onClick={() => navHistory(`/list/${0}`)}>1</button>*/}
                {/*<button>prev</button>*/}
                <div className='nav-posts-overflow'>
                    {btnNav.map((item, i) => {
                        return <button
                            ref={btnRef}
                            key={idx++}
                            className={`btn-nav-posts ${num === i * 5 ? 'active-btn' : ''}`}
                            onClick={(e) => {
                                setNum(i * 5)
                                navHistory(`/list/${i * 5}`)
                            }}
                        >{i + 1}</button>
                    })}
                </div>
                <button onClick={() => {
                    btnRef.current.style.transform = `translateX(-${40}px)`;
                    //btnRef.current.offsetWidth
                    console.log(btnRef.current.offsetWidth)
                }}>next</button>
            </div>
            <SimpleSnackbar isSnackbar={isSnackbar} color={"success"} message={'Deleted successful!'} />
        </>
    )
}


export default WithServer()(PostListItem);
