import React, { useRef, useState } from 'react';
import { useHistory } from 'react-router';
import WithServer from '../../hoc';
import { addUser, fault } from '../../../actions';

import './modalAuthorized.css';
import { useDispatch, useSelector } from 'react-redux';

const ModalAuthorized = ({ Server, cl, setModal }) => {
    //useSelector
    const error = useSelector(state => state.error)

    //useRef
    const ref = useRef()
    let clazz = cl;

    //useDispatch
    const dispatch = useDispatch()

    //useState
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [showLogin, setShowLogin] = useState(false)

    //useHistory
    const history = useHistory()
    const nav = (link) => {
        history.push(link)
    }

    const customLogin = () => {
        Server.loginUser(email, password)
            .then(res => {
                nav(`/list/${0}`)
                localStorage.setItem('token', res.data.token)
                if (localStorage.getItem('token')) {
                    Server.authorizationUser(localStorage.getItem('token'))
                        .then(res => {
                            dispatch(addUser(res.data))
                            let saveUser = JSON.stringify(res.data)
                            localStorage.setItem('user', saveUser)
                        })
                }
            })
            .catch(e => {
                dispatch(fault())
            })
    }

    return (
        <>
            <div className={`modal-liked ${clazz}`} ref={ref}>
                <div className='header-modal-liked'>
                    <p className='modal-liked-title'>Do u like post?</p>
                    <button className='close-modal-like' onClick={() => setModal('hidden')}>X</button>
                </div>
                <p className='login-first'>login first</p>
                <hr />
                <button className='login-modal-liked' onClick={() => setShowLogin(true)}>Sign in</button>
            </div>

            <div className={`small-login-form ${showLogin ? 'show' : 'hidden'} hidden`}>
                <div className="modal-authorization">
                    <form onSubmit={(e) => e.preventDefault()}>
                        <div className="form-group">
                            <label>Email address</label>
                            <input
                                type="email"
                                className="form-control"
                                aria-describedby="emailHelp"
                                placeholder="Enter email"
                                title='Enter your email'
                                value={email}
                                onChange={(e) => { setEmail(e.target.value) }} />
                            <small className="form-text text-muted"></small>
                        </div>
                        <div className="form-group">
                            <label>Password</label>
                            <input
                                type="password"
                                className="form-control"
                                placeholder="Password"
                                title='Enter your password'
                                value={password}
                                onChange={(e) => setPassword(e.target.value)} />
                            <div className="auth-group">
                                <button className="forgotPass authHover" onClick={(e) => e.preventDefault()}>Forgot password</button>
                                <button className="signUp authHover" onClick={() => nav("/signUp")}>sign up</button>
                            </div>

                            <small className="form-text text-muted"></small>
                        </div>
                        <button type="submit" className="cancel" onClick={() => nav(`/list/${0}`)}>Сancel</button>
                        <button type="submit" className="confirm" onClick={customLogin}>Sign In</button>
                        {error && <p className='error'>Что-то пошло не так....</p>}
                    </form>
                </div>
            </div >
        </>
    )
}

export default WithServer()(ModalAuthorized);
