import React from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

import './registrationForm.css';


const LoginForm = () => {

    let history = useHistory();

    const signUp = () => {
        history.push("/signUp")
    }

    //const main = () => {
    //    history.push("/list");
    //}

    let signInUsers = {
        email: '',
        password: ''
    }

    const emailUser = (e) => {
        signInUsers.email = e.target.value
    }

    const passwordUser = (e) => {
        signInUsers.password = e.target.value
    }

    async function signInUser(data) {
        try {
            const response = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", data);
            console.log('👉 Returned data:', response);
        } catch (e) {
            console.log(`😱 Axios request failed: ${e}`);
        }
    }

    return (
        <div className="post-list">
            <div className="modal-registration">
                <form>
                    <div className="form-group">
                        <label>Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            title='Enter your email'
                            onChange={emailUser} />
                        <small className="form-text text-muted"></small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            title='Enter your password'
                            onChange={passwordUser} />
                        <small className="form-text text-muted"></small>
                    </div>
                    <button type="submit" className="btn btn-primary up" onClick={signUp}>Sign up</button>
                    <button type="submit" className="btn btn-primary in" onClick={(e) => {
                        e.preventDefault();
                        signInUser(signInUsers)
                    }}>Sign in</button>
                </form>
            </div>
        </div>
    )
}


export default LoginForm;
