    //}

    async loginUser(email, password, nav, dispatch) {
        await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", {
            email,
            password,
        })
            .then(res => {
                nav(`/list/page${1}/?limit=10&skip=${0}`)
                localStorage.setItem('token', res.data.token)
                if (localStorage.getItem('token')) {
                    Server.authorizationUser(localStorage.getItem('token'))
                        .then(res => {
                            dispatch(addUser(res.data))
                            let saveUser = JSON.stringify(res.data)
                            localStorage.setItem('user', saveUser)
                        })
                }
            })
            .catch(e => {
                nav('/login')
                dispatch(fault())
                console.log(e.response)
            })

    }

    async authorizationUser(token) {
        try {
            const _res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth/user', { headers: { "Authorization": `Bearer ${token}` } })
            return _res
        } catch (e) {
            console.log(e.response);
        }
    }

    async createNewUser(email, password, name, history, dispatch) {
        try {
            await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/users", {
                email,
                password,
                name
            })
                .then(res => setTimeout(() => history.push('/login'), 500))
                .catch(e => dispatch(fault()))
        } catch (e) {
            console.log(e.response);
        }
    }

    async getAllUsers() {
        try {
            const res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/users')
            return res
        } catch (e) {
            console.log(e.response);
        }
    }

    async getAllPosts(skip = 0, limit = 10) {
        try {
            const res = await axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts?limit=${limit}&skip=${skip}`)
            return res
        } catch (e) {
            console.log(e.response)
        }
    }

    async getPost(id) {
        try {
            const res = await axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`)
            return res
        } catch (e) {
            console.log(e.response)
        }
    }


    async getUser(id) {
        try {
            const res = await axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`)
            return res
        } catch (e) {
            return e.response
        }
    }

    async setLikePost(id, token) {
        try {
            const res = await axios({
                method: 'PUT',
                url: `https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/like/${id}`,
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            })
            return res
        } catch (e) {
            console.log(e.response)
        }
    }

    async getImage(ref, image, defaultImage, alt) {
        await axios.get(`https://nodejs-test-api-blog.herokuapp.com${image}`)
            .then(res => {
                if (res.statusText === 'OK') {
                    ref.current.src = `https://nodejs-test-api-blog.herokuapp.com${image}`
                }
            })
            .catch(e => {
                if (e.status >= 400) {
                    ref.current.src = defaultImage;
                    ref.current.alt = alt;
                }
            })
    }

    async deletePost(id, token) {
        try {
            const res = await axios.delete(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`, { headers: { "Authorization": `Bearer ${token}` } });
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response)
        }
    }


    async deleteUser(id, token) {
        try {
            const res = await axios.delete(`https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`, { headers: { "Authorization": `Bearer ${token}` } });
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response)
        }
    }

    async createNewPost(title, fullText, description, token) {
        try {
            const res = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/posts",
                {
                    title,
                    fullText,
                    description,
                },
                {
                    headers:
                        { "Authorization": `Bearer ${token}` }
                });
            return res
        } catch (e) {
            //console.log(e.response);
            const error = e.response
            return error
        }
    }


    async updateUserName(id, token, name) {
        try {
            const res = await axios({
                method: 'PATCH',
                url: `https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`,
                data:
                {
                    name,
                },

                headers:
                    { "Authorization": `Bearer ${token}` }

            });
            return res
        } catch (e) {
            console.log(e.response)
        }
    }

    async updateUserAvatar(id, token, avatar) {
        try {
            const res = await axios.put(`https://nodejs-test-api-blog.herokuapp.com/api/v1/users/upload/${id}`, avatar, {
                headers: {
                    "Authorization": `Bearer ${token}`,
                    "Content-Type": ' multipart/form-data'
                }
            }
            )
            return res
        } catch (e) {
            console.log(e.response)
        }
    }

    async updatePost(id, token, title, description, fullText) {
        try {
            const res = await axios({
                method: 'PATCH',
                url: `https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`,
                data:
                {
                    title,
                    description,
                    fullText,
                },

                headers:
                    { "Authorization": `Bearer ${token}` }

            })
            return res
        } catch (e) {
            console.log(e.response)
        }
    }

    async updatePostImage(id, token, image) {
        try {
            const res = await axios.put(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/upload/${id}`, image, {
                headers: {
                    "Authorization": `Bearer ${token}`,
                    "Content-Type": ' multipart/form-data'
                }
            }
            )
            return res
        } catch (e) {
            console.log(e.response)
        }
    }


}
