import React, { useEffect, useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { logout, addUser } from '../../actions';
import WithServer from '../hoc';
import SimpleSnackbar from '../snackbar';

import './app-header.css';

const AppHeader = ({ Server }) => {
    const defaultAvatar = 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'
    //const login = useSelector(state => state.login)
    const [show, setShow] = useState(false)
    // Hook
    const dispatch = useDispatch()
    const user = useSelector(state => state.user)
    const history = useHistory();

    const refAvatar = useRef()
    const refTabs = useRef()

    // history push
    const nav = (link) => {
        history.push(link);
    }

    // localStorage 
    const isAuthorized = localStorage.getItem('token');
    const isAuthorizedUser = JSON.parse(localStorage.getItem('user'))

    //logout
    const logOut = () => {
        dispatch(logout());
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        nav("/login");
    }

    // if user - false , dispatch user in state
    useEffect(() => {
        if (Object.keys(user).length === 0 && isAuthorized) {
            dispatch(addUser(isAuthorizedUser))
        }
    }, [dispatch, isAuthorized, isAuthorizedUser, user])

    // if user = true, get avatar
    useEffect(() => {
        if (user && isAuthorized) {
            Server.getImage(refAvatar, user.avatar, defaultAvatar, '')
                .then(res => console.log(res))
                .catch(e => console.log(e.response))
        }

    }, [Server, isAuthorized, user])


    const navUser =
        <div className='nav-user' onClick={() => {
            refTabs.current.classList.toggle('trans-tabs')
        }}>
            <img src={defaultAvatar} className="user-avatar" alt="Your prof" ref={refAvatar} />
            <button className='menu-user' >
                <i className="fa fa-angle-up fa-1x"></i>
            </button>
            <div className='test-tabs' ref={refTabs}>
                <ul className='list-unstyled ps-0'>
                    <li className='mb-1'>
                        <button onClick={() => nav('/editor-posts')}>Your posts</button>
                    </li>
                    <li className='mb-1'>
                        <button onClick={() => nav('/editor-profile')}>Edit profile</button>
                    </li>
                    <li className='mb-1'>
                        <button onClick={() => nav('/create-post')}>Create new post</button>
                    </li>
                    <li className='mb-1'>
                        <button onClick={() => console.log('Network...')}>Settings</button>
                    </li>
                    <li className='mb-1'>
                        <button onClick={() => logOut()}>Log out</button>
                    </li>

                </ul>
            </div>
        </div>

    return (
        <header className="header">
            <h1 className='h3 text-light' onClick={() => nav('/')}>PIPITER</h1>
            <nav className="navbar navbar-light">
                <form className="form-inline" onSubmit={(e) => e.preventDefault()}>
                    <input className="form-control mr-sm-2 " type="search" placeholder="Search" aria-label="Search" />
                    <button className="btn btn-outline-secondary my-2 my-sm-0" type="submit" onClick={() => nav('/list')}>Search</button>
                </form>
            </nav>
            {
                user && isAuthorized ?
                    //<button className="log btn btn-dark" onClick={() => logOut()}>Log out</button> :
                    navUser :
                    <button className="log btn btn-dark" onClick={() => nav("/login")}>Sign in</button>
            }
            <SimpleSnackbar isDeleted={show} />
        </header>
    )
}



export default WithServer()(AppHeader);
