import React from 'react';
import AppHeader from '../app-header';
import { MainPage, LoginPage, ListPage, RegisterPage, PostPage, CreatePage, EditorPostsPage, EditorProfilePage, UserPostsPage } from '../pages';
import { Route, Switch } from 'react-router-dom';
import SocialShare from '../social-share';

export default function App() {
    return (
        <>
            <React.StrictMode>
                <AppHeader />
                <SocialShare />
                <Switch>
                    <Route path='/' exact component={MainPage} />
                    <Route path='/login' exact component={LoginPage} />
                    <Route path='/list' exact component={ListPage} />
                    <Route path='/signUp' exact component={RegisterPage} />
                    <Route path='/post/:id' exact component={PostPage} />
                    <Route path='/create-post' exact component={CreatePage} />
                    <Route path='/editor-posts/:id' exact component={EditorPostsPage} />
                    <Route path='/editor-profile' exact component={EditorProfilePage} />
                    <Route path='/user-post/:id' exact component={UserPostsPage} />
                </Switch>
            </React.StrictMode>
        </>
    );
}
