import { POSTS, LOADED, ERROR } from './types'

const initialState = {
    postOnPage: [],
    loading: true,
    error: false,
}

function postsReducer(state = initialState, action) {
    switch (action.type) {
        case POSTS:
            if (state.postOnPage === []) {
                return {
                    ...state,
                }
            }
            return {
                ...state,
                postOnPage: action.payload,
            }
        case LOADED:
            return {
                ...state,
                loading: false
            }
        case ERROR:
            return {
                ...state,
                //error: action.payload
                error: true
            }
        default: return state;
    }

}

export default postsReducer;
