import React, { useEffect, useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router';
import { loaded } from '../../actions';
import WithServer from '../hoc';


import Spinner from '../spinner';
import './editor-posts.sass';

const EditorPosts = ({ Server }) => {
    //useSelector
    const loading = useSelector(state => state.loading)

    //useParams
    const { id } = useParams()

    //useDispatch
    const dispatch = useDispatch();

    //useState
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [fullText, setFullText] = useState('')
    const [message, setMessage] = useState('')
    const [post, setPost] = useState(null)

    //ref
    const refTitle = useRef()
    const refDescription = useRef()
    const refFullText = useRef()
    const refImagePost = useRef()
    const refMessage = useRef()

    // token 
    const token = localStorage.getItem('token')

    const changeField = (ref) => {
        ref.current.disabled = '';
        ref.current.focus()
    }

    const messageResponse = (ref, message, color) => {
        ref.current.classList.add(color)
        setMessage(message)
        setTimeout(() => setMessage(''), 1500)
    }

    const changePost = (id, token, title, description, fullText) => {
        Server.updatePost(id, token, title, description, fullText)
            .then(res => {
                console.log(res)
                messageResponse(refMessage, 'Sucсess', 'green')
            })
            .catch(e => messageResponse(refMessage, 'Something went wrong', 'red'))
    }

    const changePostImage = (e) => {
        let formData = new FormData()
        formData.append('image', e.target.files[0])
        Server.updatePostImage(id, token, formData)
            .then(res => {
                messageResponse(refMessage, 'Sucсess', 'green')
                Server.getPost(id)
                    .then(res => Server.getImage(refImagePost, res.data.image))
            })
            .catch(e => messageResponse(refMessage, 'Something went wrong', 'red'))
    }

    useEffect(() => {
        Server.getPost(id)
            .then(res => {
                dispatch(loaded())
                setPost(res.data)
                Server.getImage(refImagePost, res.data.image, '', res.data.description)
            })
            .catch(res => setPost(null))
    }, [Server, dispatch, id])

    if (loading || !post) {
        return <Spinner />
    }
    return (
        <div className='editor'>

            <div className='editor-body'>

                <div className="item">
                    <div className="item-post">
                        <div className="post-item-list">
                            <i class="fa fa-ellipsis-v"></i>
                            <div className="info-post">
                                <div className='header-post '>
                                    <input type="text"
                                        title='Title your post'
                                        className="title editor-title"
                                        placeholder={post.title}
                                        disabled='disabled'
                                        onBlur={(e) => e.target.disabled = 'disabled'}
                                        ref={refTitle}
                                        value={title}
                                        onChange={(e) => setTitle(e.target.value)}
                                    />
                                    <button className='btn-update-post' onClick={() => changeField(refTitle)}>
                                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-pen-fill" viewBox="0 0 16 16">
                                            <path d="m13.498.795.149-.149a1.207 1.207 0 1 1 1.707 1.708l-.149.148a1.5 1.5 0 0 1-.059 2.059L4.854 14.854a.5.5 0 0 1-.233.131l-4 1a.5.5 0 0 1-.606-.606l1-4a.5.5 0 0 1 .131-.232l9.642-9.642a.5.5 0 0 0-.642.056L6.854 4.854a.5.5 0 1 1-.708-.708L9.44.854A1.5 1.5 0 0 1 11.5.796a1.5 1.5 0 0 1 1.998-.001z" />
                                        </svg>
                                    </button>
                                </div>
                                <div className='editor-description'>
                                    <input
                                        title='Description your post'
                                        type="text"
                                        className=" editor-description"
                                        placeholder={post.description}
                                        disabled='disabled'
                                        onBlur={(e) => e.target.disabled = 'disabled'}
                                        ref={refDescription}
                                        value={description}
                                        onChange={(e) => setDescription(e.target.value)}
                                    />
                                    <button className='btn-update-post' onClick={() => changeField(refDescription)}>
                                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-pen-fill" viewBox="0 0 16 16">
                                            <path d="m13.498.795.149-.149a1.207 1.207 0 1 1 1.707 1.708l-.149.148a1.5 1.5 0 0 1-.059 2.059L4.854 14.854a.5.5 0 0 1-.233.131l-4 1a.5.5 0 0 1-.606-.606l1-4a.5.5 0 0 1 .131-.232l9.642-9.642a.5.5 0 0 0-.642.056L6.854 4.854a.5.5 0 1 1-.708-.708L9.44.854A1.5 1.5 0 0 1 11.5.796a1.5 1.5 0 0 1 1.998-.001z" />
                                        </svg>
                                    </button>
                                </div>
                                <div className='editor-image'>
                                    <img src='' alt='post_image' className="post-image" ref={refImagePost} />
                                    <label htmlFor='file-upload' className='inp-upload' >
                                        <i className="fa fa-camera"></i>
                                        <input
                                            type='file'
                                            id="file-upload"
                                            onChange={(e) => changePostImage(e)}
                                        />
                                    </label>
                                </div>
                                <div className='editor-full-text'>
                                    <textarea
                                        title='Full text your post'
                                        className="form-control editor-full-text"
                                        rows="2"
                                        placeholder={post.fullText}
                                        disabled='disabled'
                                        onBlur={(e) => e.target.disabled = 'disabled'}
                                        ref={refFullText}
                                        value={fullText}
                                        onChange={(e) => setFullText(e.target.value)}
                                    >
                                    </textarea>
                                    <button className='btn-update-post' onClick={() => changeField(refFullText)}>
                                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-pen-fill" viewBox="0 0 16 16">
                                            <path d="m13.498.795.149-.149a1.207 1.207 0 1 1 1.707 1.708l-.149.148a1.5 1.5 0 0 1-.059 2.059L4.854 14.854a.5.5 0 0 1-.233.131l-4 1a.5.5 0 0 1-.606-.606l1-4a.5.5 0 0 1 .131-.232l9.642-9.642a.5.5 0 0 0-.642.056L6.854 4.854a.5.5 0 1 1-.708-.708L9.44.854A1.5 1.5 0 0 1 11.5.796a1.5 1.5 0 0 1 1.998-.001z" />
                                        </svg>
                                    </button>
                                </div>
                                <p className='message' ref={refMessage}>{message}</p>
                                <button className='btn btn-primary save' onClick={() => changePost(id, token, title, description, fullText)}>Save</button>
                            </div>
                        </div>
                    </div>
                </div>

            </div>

        </div>
    )
}

export default WithServer()(EditorPosts);
