import React, { useEffect, useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory, useLocation, useParams } from 'react-router-dom';
import { logout, addUser, posts } from '../../actions';
import WithServer from '../hoc';

//import './app-header.css';
import './app-header.sass';

const AppHeader = ({ Server }) => {
    const defaultAvatar = 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'

    function useQuery() {
        return new URLSearchParams(useLocation().search);
    }
    let query = useQuery()
    let skip = query.get('skip')
    let limit = query.get('limit')
    let { skips } = useParams()


    //useSelector
    const user = useSelector(state => state.user)

    //useDispatch
    const dispatch = useDispatch()

    //useRef
    const refAvatar = useRef()
    const refMenuList = useRef()
    const reftHamburgerBtn = useRef()
    const refSearch = useRef()
    const refSearchBtn = useRef()
    const refSearchDesktop = useRef()

    // history
    const history = useHistory();

    //useState
    const [allPosts, setAllPosts] = useState([]);
    const [showSearch, setShowSearch] = useState(false)
    const [showBurger, setShowBurger] = useState(false)
    const [foundMessage, setFoundMessage] = useState(false);
    const [foundPost, setFoundPost] = useState([])
    const [showSearchDesktop, setShowSearchDesktop] = useState(false)

    //filterPost 
    const filterPost = (posts, term) => {
        return posts.filter(post => {
            return post.title.indexOf(term) > -1
        })
    }
    // Search Posts

    const autoSerachPost = (allPosts, searchText) => {
        let foundPost = filterPost(allPosts, searchText)
        dispatch(posts(foundPost))
        foundPost.length === 0 ? setFoundMessage(true) : setFoundMessage(false);
        if (searchText.length === 0) {
            Server.getAllPosts(0)
                .then(res => dispatch(posts(res.data)))
        }
    }

    useEffect(() => {
        if (allPosts.length === 0) {
            Server.getAllPosts(0, 0)
                .then(res => setAllPosts(res.data))
        }
    }, [Server, allPosts, dispatch])

    // navgition
    const nav = (link) => {
        history.push(link);
    }

    const navDesktop = (link, event) => {
        nav(link)
        setShowSearchDesktop(false)
    }

    const navMobile = (link) => {
        nav(link)
        setShowBurger(false)
    }

    // localStorage 
    const isAuthorized = localStorage.getItem('token');
    const isAuthorizedUser = JSON.parse(localStorage.getItem('user'))

    //logout
    const logOut = () => {
        dispatch(logout());
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        nav("/login");
    }

    // if user - false , dispatch user in state
    const chekUserInState = (obj) => {
        for (let key in obj) {
            return false
        }
        return true
    }
    useEffect(() => {
        if (chekUserInState(user)) {
            dispatch(addUser(isAuthorizedUser))
        } else {
            return
        }
    }, [dispatch, isAuthorized, isAuthorizedUser, user])

    // if user = true, get avatar
    useEffect(() => {
        if (user && isAuthorized) {
            Server.getImage(refAvatar, user.avatar, defaultAvatar, '')
        } else {
            return
        }
    }, [Server, isAuthorized, user])

    //Close outside searchMobile and Hamburger
    useOnClickOutside(refSearch, (e) => {
        if (refSearchBtn.current && !refSearchBtn.current.contains(e.target))
            setShowSearch(false)
    });

    useOnClickOutside(refSearchDesktop, (e) => {
        //if (refSearchDesktopBtn.current && !refSearchDesktopBtn.current.contains(e.target))
        setShowSearchDesktop(false)
    });

    useOnClickOutside(refMenuList, (e) => {
        if (reftHamburgerBtn.current && !reftHamburgerBtn.current.contains(e.target))
            setShowBurger(false)
    });

    function useOnClickOutside(ref, handler) {
        useEffect(
            () => {
                const listener = (event) => {
                    if (!ref.current || ref.current.contains(event.target)) {
                        return;
                    }
                    handler(event);
                };
                document.addEventListener("mousedown", listener);
                document.addEventListener("touchstart", listener);
                return () => {
                    document.removeEventListener("mousedown", listener);
                    document.removeEventListener("touchstart", listener);
                };
            },
            [ref, handler]
        );
    }

    //Dropdown desktop
    const navUser =
        <>
            <div className='nav-user'>
                <img src={defaultAvatar} className="user-avatar" alt="Your prof" ref={refAvatar} />
                <button className='menu-user' >
                    <i className="fa fa-angle-up fa-1x"></i>
                </button>
                <div className='test-tabs'>
                    <ul className='list-unstyled ps-0'>
                        <li className='mb-1'>
                            <button onClick={() => nav(`/user-posts/${user._id}`)}>
                                <i className="fa fa-envelope"></i>Your posts</button>
                        </li>
                        <li className='mb-1'>
                            <button onClick={() => nav('/editor-profile')}>
                                <i className="fa fa-user-circle"></i>Edit profile</button>
                        </li>
                        <li className='mb-1'>
                            <button onClick={() => nav('/create-post')}>
                                <i className="fa fa-plus"></i>Create post</button>
                        </li>
                        <li className='mb-1'>
                            <button onClick={() => console.log('Network...')}>
                                <i className="fa fa-cog"></i>Settings</button>
                        </li>
                        <li><hr /></li>
                        <li className='mb-1'>
                            <button onClick={() => logOut()}>
                                <i className="fa fa-chevron-right"></i>Log out</button>
                        </li>

                    </ul>
                </div>
            </div>

            {/*Dropdown mobile*/}

            <div className='burger-nav'>
                <button ref={refSearchBtn} type="button" className="mobile-search" onClick={() => setShowSearch(!showSearch)}>
                    {showSearch
                        ? <p>X</p>
                        : <i className="fa fa-search"></i>}
                </button>
                <div className={`hamburger ${showBurger && 'hamburger-active'}`} ref={reftHamburgerBtn} onClick={() => setShowBurger(!showBurger)}>
                    <span></span>
                    <span></span>
                    <span></span>
                </div>
                <div className={`burger-menu ${showBurger && 'burger-active'}`} ref={refMenuList}>
                    <div className='menu-list'>
                        <ul className='list-unstyled ps-0'>
                            <li className='mb-1'>
                                <button onClick={() => navMobile(`/user-posts/${user._id}`)}>
                                    <i className="fa fa-envelope"></i>Your posts</button>
                            </li>
                            <li className='mb-1'>
                                <button onClick={() => navMobile('/editor-profile')}>
                                    <i className="fa fa-user-circle"></i>Edit profile</button>
                            </li>
                            <li className='mb-1'>
                                <button onClick={() => navMobile('/create-post')}>
                                    <i className="fa fa-plus"></i>Create post</button>
                            </li>
                            <li className='mb-1'>
                                <button onClick={() => console.log('Network...')}>
                                    <i className="fa fa-cog"></i>Settings</button>
                            </li>
                            <li className='mb-1'>
                                <button onClick={() => logOut()}>
                                    <i className="fa fa-chevron-right"></i>Log out</button>
                            </li>

                        </ul>
                    </div>
                </div>
            </div>
        </>


    return (
        <>
            <header className="header" onClick={() => console.log(skips)}>
                <button className='header-logo' onClick={() => nav(`/list?limit=10&skip=0`)}>
                    <span className='logo1'>Ficus</span>
                    <span className='logo2'>Test</span>
                </button>
                <nav className="navbar navbar-light" ref={refSearchDesktop}>
                    <form onSubmit={(e) => e.preventDefault()}>
                        <input
                            type='search'
                            className="form-control search-panel"
                            placeholder='Search...'
                            onChange={(e) => {
                                setFoundPost(filterPost(allPosts, e.target.value))
                                e.target.value === '' ? setShowSearchDesktop(false) : setShowSearchDesktop(true)
                            }}
                        />
                        <div className={`response-search ${showSearchDesktop ? 'show' : 'hidden'}`}>
                            {foundPost.map(post => <button key={post._id} onClick={() => navDesktop(`/post/${post._id}`)}>{post.title}</button>)}
                            {foundPost.length === 0 && <p className='not-found-message'>Not found...</p>}
                        </div>
                    </form>
                </nav>
                {
                    user && isAuthorized ?
                        navUser :
                        //<button className="log" onClick={() => nav("/login")}>SIGN IN</button>
                        <div className='navigation'>
                            <button ref={refSearchBtn} type="button" className="mobile-search" onClick={() => setShowSearch(!showSearch)}>
                                {showSearch
                                    ? <p>X</p>
                                    : <i className="fa fa-search"></i>}
                            </button>
                            <button className="log" onClick={() => nav("/login")}>SIGN IN</button>
                        </div>


                }
            </header>
            {/*SearchPanel*/}
            <div className={`inp-search ${showSearch && 'search-active'}`} ref={refSearch}>
                <div className="form-outline">
                    <input
                        type="search"
                        id="form1"
                        className="form-control"
                        placeholder='Search...'
                        onBlur={(e) => e.target.value = ''}
                        onChange={(e) => autoSerachPost(allPosts, e.target.value)}
                    />
                </div>
            </div>
            {foundMessage && <p className='not-found-message'>Not found...</p>}
        </>
    )
}



export default WithServer()(AppHeader);
