import axios from 'axios';

export default class Server {

    async loginUser(email, password) {
        try {
            const res = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", {
                email,
                password,
            });
            console.log('👉 Returned data:', res);
            return res
        } catch (e) {
            console.log(`😱 Axios request failed: ${e}`);
        }

    }

    async authorizationUser(token) {
        try {
            const _res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth/user', { headers: { "Authorization": `Bearer ${token}` } })
            return _res
        } catch (e) {
            console.log(e)
        }
    }

    async createNewUser(email, password, name) {
        try {
            const res = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/users", {
                email,
                password,
                name
            });
            console.log('👉 Returned data:', res);
            return res
        } catch (e) {
            console.log(e.response);
            console.log(`😱 Axios request failed: ${e}`);

        }
    }

    async getAllUsers() {
        try {
            const res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/users')
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response);
            console.log(`😱 Axios request failed: ${e}`);
        }
    }

    async getAllPosts() {
        try {
            const res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/posts')
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response)
            console.log(`😱 Axios request failed: ${e}`);
        }
    }

    async getPost(id) {
        try {
            const res = await axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`)
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response)
            console.log(`😱 Axios request failed: ${e}`);
        }
    }


    async getUser(id) {
        try {
            const res = await axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`)
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response)
            console.log(`😱 Axios request failed: ${e}`);
        }
    }

    async setLikePost(id, token) {
        try {
            const res = await axios({
                method: 'PUT',
                url: `https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/like/${id}`,
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            })
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response)
        }
    }

    async getAvatarUser(ref, userAvatar) {
        await axios.get(`https://nodejs-test-api-blog.herokuapp.com${userAvatar}`)
            .then(res => {
                if (res.statusText === 'OK') {
                    ref.current.src = `https://nodejs-test-api-blog.herokuapp.com${userAvatar}`
                }
            })
            .catch(e => {
                ref.current.src = 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'
            })
    }
}
