import React, { useEffect, useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { logout, addUser } from '../../actions';
import WithServer from '../hoc';
import SearchPanel from './search-panel'

import './app-header.css';

const AppHeader = ({ Server }) => {
    const defaultAvatar = 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'
    //const login = useSelector(state => state.login)

    // Hook
    const dispatch = useDispatch()
    const user = useSelector(state => state.user)
    const history = useHistory();

    // Search Posts
    const [allPosts, setAllPosts] = useState([]);
    const [searchText, setSearchText] = useState('')

    const filterPost = (posts, term) => {
        return posts.filter(post => {
            return post.title.indexOf(term) > -1
        })
    }

    const searchPost = (allPosts, searchText) => {
        let foundPost = filterPost(allPosts, searchText)
        if (searchText) {
            nav(`/post/${foundPost[0]._id}`)
        } else {
            nav(`/list/${0}`)
        }
    }

    useEffect(() => {
        if (allPosts.length === 0) {
            Server.getAllPosts(0, 0)
                .then(res => setAllPosts(res.data))
        }
    }, [Server, allPosts])
    //

    const refAvatar = useRef()
    const refTabs = useRef()

    // history push
    const nav = (link) => {
        history.push(link);
    }

    // localStorage 
    const isAuthorized = localStorage.getItem('token');
    const isAuthorizedUser = JSON.parse(localStorage.getItem('user'))

    //logout
    const logOut = () => {
        dispatch(logout());
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        nav("/login");
    }

    // if user - false , dispatch user in state
    const chekUserInState = (obj) => {
        for (let key in obj) {
            return false
        }
        return true
    }
    useEffect(() => {
        if (chekUserInState(user)) {
            dispatch(addUser(isAuthorizedUser))
        } else {
            return
        }
    }, [dispatch, isAuthorized, isAuthorizedUser, user])

    // if user = true, get avatar
    useEffect(() => {
        if (user && isAuthorized) {
            Server.getImage(refAvatar, user.avatar, defaultAvatar, '')
            //.then(res => console.log(res))
            //.catch(e => console.log(e.response))
        } else {
            return
        }
    }, [Server, isAuthorized, user])




    const navUser =
        <div className='nav-user' onClick={() => {
            refTabs.current.classList.toggle('trans-tabs')
        }}>
            <img src={defaultAvatar} className="user-avatar" alt="Your prof" ref={refAvatar} />
            <button className='menu-user' >
                <i className="fa fa-angle-up fa-1x"></i>
            </button>
            <div className='test-tabs' ref={refTabs}>
                <ul className='list-unstyled ps-0'>
                    <li className='mb-1'>
                        <button onClick={() => nav(`/user-posts/${user._id}`)}>Your posts</button>
                    </li>
                    <li className='mb-1'>
                        <button onClick={() => nav('/editor-profile')}>Edit profile</button>
                    </li>
                    <li className='mb-1'>
                        <button onClick={() => nav('/create-post')}>Create new post</button>
                    </li>
                    <li className='mb-1'>
                        <button onClick={() => console.log('Network...')}>Settings</button>
                    </li>
                    <li className='mb-1'>
                        <button onClick={() => logOut()}>Log out</button>
                    </li>

                </ul>
            </div>
        </div>

    return (
        <header className="header">
            <div className='header-logo' onClick={() => nav(`/list/${0}`)}>
                <span className='logo1'>Ficus</span>
                <span className='logo2'>Test</span>
            </div>
            <nav className="navbar navbar-light">
                <form className="form-inline" onSubmit={(e) => e.preventDefault()}>
                    <SearchPanel posts={allPosts} setSearchText={setSearchText} searchPost={searchPost} />
                    <button
                        className="search"
                        type="submit"
                        onClick={() => searchPost(allPosts, searchText)}>
                        <i className="fa fa-search"></i>
                    </button>
                </form>
            </nav>
            {
                user && isAuthorized ?
                    navUser :
                    <button className="log" onClick={() => nav("/login")}>SIGN IN</button>
            }
        </header>
    )
}



export default WithServer()(AppHeader);
