import axios from 'axios';
import { POSTS, LOADED, ERROR, DELETED } from './types'

//const posts = (posts) => {
//    return (dispatch) =>
//        dispatch({ type: POSTS, payload: posts })
//}

const posts = (skip = 0, limit = 10) => {
    return (dispatch) =>
        axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts?limit=${limit}&skip=${skip}`)
            .then(res => {
                dispatch({ type: POSTS, payload: res.data })
                dispatch({ type: LOADED })
            })
            .catch(e => dispatch({ type: ERROR }))

}


const loaded = () => {
    return (dispatch) =>
        dispatch({ type: LOADED })
}

const fault = () => {
    return (dispatch) =>
        dispatch({ type: ERROR })
}

const deletePost = (action) => {
    return (dispatch) =>
        dispatch({ type: DELETED, payload: action })
}

export { posts, loaded, fault, deletePost }
