import axios from 'axios';


function loginUser(email, password) {
    return dispatch =>
        axios
            .post('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth', {
                email,
                password,
            })
            .then(res =>
                localStorage.setItem('token', res.data.token)
            )
            .catch(e => console.log(e))
}

export default loginUser
