const initialState = {
    autor: false,
    like: false,
    user: []
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'AUTOR':
            return {
                ...state,
                autor: true
            };
        case 'LIKE':
            return {
                ...state,
                like: true
            };
        default:
            return state
    }
}


export default reducer;
