import React, { useEffect, useRef, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
//import { loaded } from '../../actions';
import Spinner from '../spinner';
import SpinnerImage from './spinner-image'
import ModalConfirm from '../modalConfirm';
import { useHistory } from 'react-router';

import './editor-profile.sass';

import { loaded } from '../../redux/user/actions'


const EditorProfile = ({ Server }) => {
    const defaultAvatar = 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'

    // Modal 
    const [show, setShow] = useState(false)
    const handleClose = () => setShow(false)
    const handleShow = () => setShow(true)

    //useState
    const [name, setName] = useState('')
    const [currentUser, setUser] = useState({})
    const [loadingImage, setLoadingImage] = useState(false)

    //History
    const history = useHistory()

    //useDispatch
    const dispatch = useDispatch()

    //useSelector
    //const user = useSelector(state => state.user)
    //const loading = useSelector(state => state.loading)
    //new
    const user = useSelector(state => state.userState.user)
    const loading = useSelector(state => state.userState.loading)
    const deleteUser = useSelector(state => state.userState.deleteUser)

    //useRef
    const refAvatar = useRef()

    useEffect(() => {
        setUser(user)
    }, [dispatch, user])

    const isAuthorized = localStorage.getItem('token');

    const changeName = (id, token, name) => {
        Server.updateUserName(id, token, name, dispatch)
            .then(res => {
                setUser({ ...currentUser, name })
                setName('')
            })

    }

    const updateAvatar = (e) => {
        setLoadingImage(true)
        let formData = new FormData()
        formData.append('avatar', e.target.files[0])
        Server.updateUserAvatar(user._id, isAuthorized, formData, dispatch)
            .then(() => setLoadingImage(false))
    }

    const delUser = () => {
        Server.deleteUser(user._id, isAuthorized, dispatch, history)
            .then(res => handleClose())
    }

    const chekUserInState = (obj) => {
        for (let key in obj) {
            return false
        }
        return true
    }

    //if user = true - get avatar
    useEffect(() => {
        if (!chekUserInState(user)) {
            Server.getImage(refAvatar, user.avatar, defaultAvatar, '')
            dispatch(loaded())
        } else {
            return <Spinner />
        }
        //currentUser.avatar !== 
    }, [Server, dispatch, user, refAvatar])


    if (loading || !user || !currentUser) {
        return <Spinner />
    }
    return (
        <div className='container-editor-profile' onClick={() => console.log(dele)}>
            <form className='editor-profile' onSubmit={(e) => e.preventDefault()}>
                <header className='editor-header'>
                    <div className='editor-avatar'>
                        {/*<img alt='img' ref={refAvatar} src={defaultAvatar} />*/}
                        {loadingImage ? <SpinnerImage /> : <img alt='img' ref={refAvatar} src={defaultAvatar} />}
                        <label htmlFor='file-upload' className='inp-upload' >
                            <i className="fa fa-camera"></i>
                            <input
                                type='file'
                                accept="image/*"
                                id="file-upload"
                                onChange={(e) => updateAvatar(e)}
                            />
                        </label>
                    </div>
                    <p className='editor-name'>{currentUser.name}</p>
                </header>
                <div className='inp-name'>
                    <label >Name:</label>
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Change your name"
                        title='Enter your new name'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <button className='btn btn-primary save' onClick={() => changeName(user._id, isAuthorized, name)}>Save</button>
                <button type='button' className='btn btn-danger remove' onClick={handleShow}>Delete user</button>
            </form>
            <ModalConfirm show={show} handleClose={handleClose} remove={delUser} label={'Are you sure , what you want to delete the user?'} />
        </div>
    )
}

export default WithServer()(EditorProfile);
