const initialState = {
    error: false,
    login: false,
    user: null,
    allUsers: null,
    allPosts: [],
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'NEW_USER':
            return {
                ...state,
                user: action.payload
            }
        case 'LOGIN':
            return {
                ...state,
                login: true
            }
        case 'LOGOUT':
            return {
                ...state,
                login: false,
                user: null
            }
        case 'ERROR':
            return {
                ...state,
                error: action.payload
            }
        case 'ALL_USERS':
            return {
                ...state,
                allUsers: action.payload
            }
        case 'ALL_POSTS':
            return {
                ...state,
                allPosts: action.payload
            }
        case 'POST_ADD_TO_STATE':
            const id = action.payload;

            const postInd = state.allPosts.findIndex(post => post._id === id);
            if (postInd >= 0) {
                const postInState = state.allPosts.find(item => item._id === id);
                const newItem = {
                    ...postInState,
                }
                return {
                    ...state,
                    allPosts: [
                        ...state.allPosts.slice(0, postInd),
                        newItem,
                        ...state.allPosts.slice(postInd + 1)
                    ],
                }
            }
            // товара раньше не было в корзине
            const newPost = action.data
            return {
                ...state,
                allPosts: [
                    ...state.allPosts,
                    newPost
                ],
            };
        default:
            return state
    }
}


export default reducer;
