const initialState = {
    error: false,
    user: null,
    userToken: null
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'NEW_USER':
            return {
                ...state,
                user: action.payload
            }
        case 'LOGOUT':
            return {
                ...state,
                user: null
            }
        case 'ADD_TOKEN':
            return {
                ...state,
                userToken: action.payload
            }
        case 'REMOVE_TOKEN':
            return {
                ...state,
                userToken: null
            }
        case 'ERROR':
            return {
                ...state,
                error: action.payload
            }
        default:
            return state
    }
}


export default reducer;
