import React, { useEffect, useState } from 'react'

import './index.sass'

const DateAndTime = () => {

    const getTimeRemaining = (endtime) => {
        const t = Date.parse(endtime) - Date.parse(new Date());
        const days = Math.floor(t / (1000 * 60 * 60 * 24));
        const hours = Math.floor((t / (1000 * 60 * 60) % 24));
        const minutes = Math.floor((t / 1000 / 60) % 60);
        const seconds = Math.floor((t / 1000) % 60);

        return {
            'total': t,
            'days': days,
            'hours': hours,
            'minutes': minutes,
            'seconds': seconds
        };
    }

    const getZero = (num) => {
        if (num >= 0 && num < 10) {
            return `0${num}`;
        } else {
            return num;
        }
    }

    const [total, setTotal] = useState({})

    //useEffect(() => {
    //    setTotal(getTimeRemaining('2021-07-30'))
    //}, [])

    const setClock = () => {
        setInterval(() => {
            setTotal(getTimeRemaining('2021-07-30'))
            setDate({
                //...date,
                days: getZero(total.days),
                hours: getZero(total.hours),
                minutes: getZero(total.minutes),
                seconds: getZero(total.seconds)
            })
        }, 1000)
    }

    const [date, setDate] = useState({ days: '', hours: '', minutes: '', seconds: '' })
    useEffect(() => {
        setClock()
    }, [setClock, total])

    //const date = 

    return (
        <div className={'Date-Time'} >
            <div className='Date'>
                Fri 04.06.2021
            </div>
            <div className='Time'>
                {/*11:27:33 PM*/}
                {date.days}.{date.hours}.{date.minutes}.{date.seconds}
            </div>
        </div>
    )
}

export default DateAndTime
