import React, { useEffect, useRef, useState } from 'react';
import { useSelector } from 'react-redux';
import WithServer from '../hoc';
import ModalAuthorized from './modalAuthorized'
import { useHistory } from 'react-router';

import prof from './no-avatar.jpg'
import deletProf from './delete-user.jpg'

import './post-list.css';

const PostList = ({ Server }) => {
    const defaultAvatar = 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'

    //Hook
    const post = useSelector(state => state.post)
    const postUser = useSelector(state => state.postUser)
    const [heart, setHeart] = useState(post.likes.length)
    const history = useHistory()

    //Ref
    const refHeart = useRef()
    const refAvatar = useRef()
    const refImagePost = useRef()

    //local
    const isAuthorized = localStorage.getItem('token');
    const isAuthorizedUser = JSON.parse(localStorage.getItem('user'))

    //DateCreated
    let date = post.dateCreated
    let newDate = date.replace(/[T-Z]/g, ' ')
    newDate = newDate.replace(/\..*/, '')

    //Func and hook
    async function redHeart(likedUser, e) {
        const idxLike = await likedUser.indexOf(isAuthorizedUser._id)
        if (idxLike >= 0) {
            e.classList.add('liked')
        } else {
            e.classList.remove('liked')
        }
    }

    useEffect(() => {
        if (isAuthorizedUser) {
            Server.getPost(post._id)
                .then(res => {
                    redHeart(res.data.likes, refHeart.current)
                })
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [Server, post._id])

    const postLike = async (e) => {
        await Server.getPost(post._id)
            .then(res => {
                if (res.data.likes.length !== heart) {
                    setHeart(res.data.likes.length)
                    redHeart(res.data.likes, e)
                }
            })
    }

    async function setLike(id, token, e) {
        await Server.setLikePost(id, token)
        postLike(e)
    }

    const [modal, setModal] = useState('')


    useEffect(() => {
        if (postUser) {
            Server.getImage(refAvatar, postUser.avatar, defaultAvatar)
        }

    }, [Server, postUser])


    useEffect(() => {
        if (post) {
            Server.getImage(refImagePost, post.image, '')
        }
    }, [Server, post])

    const delPost = async (id, token) => {
        await Server.deletePost(id, token)
        history.push('/list')
    }

    return (
        <div className="item" onClick={() => console.log(isAuthorizedUser._id === postUser._id)}>
            <div className="item-post">
                <div className="post-item-list">
                    {postUser
                        ?
                        <div className="info-user">
                            <img src={prof} className="user-avatar" alt="prof" ref={refAvatar} />
                            <p> {postUser.name}</p>
                        </div>
                        :
                        <div className="info-user">
                            <img src={deletProf} className="user-avatar" alt="prof" />
                            <p className="delete-user"> User has been deleted</p>
                        </div>}
                    <div className="info-post">
                        <div className='header-post'>
                            <p className="title">{post.title}</p>
                            <button className='btn-update-post'><i class="fas fa-pen-alt"></i></button>
                        </div>
                        <img src='' alt="" className="post-image" ref={refImagePost} />
                        <ul className="full-text">{post.fullText}</ul>
                        <div className="footer-post">
                            <span className="date">{newDate}</span>
                            <div className="interaction">
                                <button
                                    className='like'
                                    onClick={() => {
                                        isAuthorized
                                            ? setLike(post._id, isAuthorized, refHeart.current)
                                            : setModal('show-flex')
                                    }
                                    }><i ref={refHeart} className="fa fa-heart"></i></button>{heart}
                                {isAuthorizedUser && isAuthorizedUser._id === postUser._id
                                    ? <button className='delete' onClick={() => delPost(post._id, isAuthorized)}><i className="fa fa-trash-o"></i></button>
                                    : null}
                            </div>
                        </div>
                    </div>

                </div>

            </div>
            <ModalAuthorized cl={modal} setModal={setModal} />
        </div>
    )
}


export default WithServer()(PostList);
